root.script:
		var CP_T =[]
		function UpdateDisplay(sCP_Tname) {
			CP_T[Game.GetLocalPlayerID()] = sCP_Tname;
			if(CP_T[Game.GetLocalPlayerID()]=="CP_1"){
				CPLabel1 = TLabel1;
			}elseif(CP_T[Game.GetLocalPlayerID()]=="CP_2"){
				CPLabel2 = TLabel2;
			}elseif(CP_T[Game.GetLocalPlayerID()]=="CP_3"){
				CPLabel3 = TLabel3;
			}elseif(CP_T[Game.GetLocalPlayerID()]=="CP_4"){
				CPLabel4 = TLabel4;
			}elseif(CP_T[Game.GetLocalPlayerID()]=="CP_5"){
				CPLabel5 = TLabel5;
			}elseif(CP_T[Game.GetLocalPlayerID()]=="CP_6"){
				CPLabel6 = TLabel6;
			}elseif(CP_T[Game.GetLocalPlayerID()]=="CP_7"){
				CPLabel7 = TLabel7;
			}else{
				TLabel1 = CPLabel1;
				TLabel2 = CPLabel2;
				TLabel3 = CPLabel3;
				TLabel4 = CPLabel4;
				TLabel5 = CPLabel5;
				TLabel6 = CPLabel6;
				TLabel7 = CPLabel7;
			}

		}
