root.script:
var votes = []
function CastVote(vote) {
	votes[Game.GetLocalPlayerID()] = vote;

	//could be improved
	var panel1 = $("#Vote1")
	var panel2 = $("#Vote2")
	var panel3 = $("#Vote3")
	if (vote == 12) { panel1.AddClass("selected"); panel2.RemoveClass("selected"); panel3.RemoveClass("selected"); }
	if (vote == 24) { panel1.RemoveClass("selected"); panel2.AddClass("selected"); panel3.RemoveClass("selected"); }
	if (vote == 36) { panel1.RemoveClass("selected"); panel2.RemoveClass("selected"); panel3.AddClass("selected"); }
}
function ConfirmVote() {
	var pid = Game.GetLocalPlayerID()
	if (votes[pid] != undefined) {
		$.GetContextPanel().RemoveAndDeleteChildren()
		GameEvents.SendCustomGameEventToServer("player_vote", {playerID: Game.GetLocalPlayerID(), vote: votes[pid]});
	}
}

(function(){
	GameEvents.Subscribe("remove_voting_screen", function(args) { $.GetContextPanel().RemoveAndDeleteChildren() })
})()
